            VALUES ('Ciudad Real', '2024-08-27', 'Reglamento_organico_de_la_Comision_Especial_de_Sugerencias_y_Reclamaciones', 'NULL', 'NULL', 'https://www.ciudadreal.es/documentos/transparencia/normativa/Reglamento_organico_de_la_Comision_Especial_de_Sugerencias_y_Reclamaciones.pdf', 'B  O  P
